  const navigate = useNavigate();
   const {sendRequest, status} = useHttp(addQuote)
useEffect(() => {
  if (status === "completed")
  navigate("/quotes")
}, [status, navigate] )

  const quoteDataHandler = (quoteData) => {
   sendRequest(quoteData)
    navigate("/quotes");
  };
