import { Fragment } from "react";
import {useLocation, Link, Route, Routes, useParams } from "react-router-dom";
import Comments from "../components/comments/Comments";
import HighlightedQuote from "../components/quotes/HighlightedQuote";

const Dummy_Quotes = [
  {
    id: "q1",
    author: "Nikhil Rai",
    text: `
  “Two things are infinite the universe and human stupidity and Im not sure about the universe.”`,
  },
  {
    id: "q2",
    author: "aditya singh",
    text: ` 
    “Be who you are and say what you feel, because those who mind don't matter, and those who matter don't mind.”`,
  },
];

const QuotesDetails = () => {
  const location = useLocation();
  const param = useParams();
  const quotes = Dummy_Quotes.find((quote) => quote.id === param.quotesId);

  const isIdLocation = location.pathname.includes(`${param.quotesId}`)
  if (!quotes) {
    return <p>no quote found!</p>;
  }
  return (
    <Fragment>
      <HighlightedQuote text={quotes.text} author={quotes.author} id ={quotes.id}/>

{  isIdLocation &&
        <div className="centered">
     <Link className="btn--flat" to="comments" >add comment</Link>
     </div>
       }

      <Routes>

        <Route path="comments" element={<Comments/>} />
      </Routes>
    </Fragment>
  );
};

export default QuotesDetails;
